function getElement(a){return document.querySelector(a)}
function getElements(a){return document.querySelectorAll(a)}
let h=HTMLElement.prototype;
h.removeAttributes=function(a){a.forEach(b=>{this.removeAttribute(b)});return this};
h.getDataset=function(a){return this.dataset[a]};
h.getDatasets=function(){};
h.setDataset=function(a,b){this.dataset[a]=b;return this};
h.removeDataset=function(a){this.removeAttribute('data-'+a);return this};
h.removeDatasets=function(a){a=Array.isArray(a)?a:a.split(' ');a.forEach(b=>{this.removeAttribute('data-'+b);});return this};
h.getClasses=function(){return Array.from(this.classList)};
h.addClass=function(a){this.classList.add(a);return this};
h.removeClass=function(a){this.classList.remove(a);return this};
h.removeClasses=function(a){a=Array.isArray(a)?a:a.split(' ');a.forEach(b=>{this.classList.remove(b)});return this};
h.toggleClass=function(a){this.classList.remove(a);return this};
h.switchClasses=function(a,b){const cl=this.classList;if(cl.contains(a)){cl.add(b);cl.remove(a)}else{cl.add(a);cl.remove(b)}return this};
h.replaceClass=function(a,b){const cl=this.classList;if(cl.contains(a)){cl.add(b);cl.remove(a)}return this};
h.hasClass=function(a){return this.classList.contains(a)};
h.hasClasses=function(a){a=Array.isArray(a)?a:a.split(' ');return a.every(b=>this.classList.contains(b))};
h.setStyle=function(a,b){this.style.setProperty(a,b);return this};
h.getStyle=function(a){if(a.startsWith('--')){const b=window.getComputedStyle(this);return b.getPropertyValue(a)}return this.style[a]};
h.removeStyle=function(a){this.style.removeProperty(a);return this};
h.removeStyles=function(a){a=Array.isArray(a)?a:a.split(' ');a.forEach(b=>{this.style.removeProperty(b);});return this};
